View CreateLoginForm ()
{
  var usernameEntry = new Entry {Placeholder = "Username"};
  var passwordEntry = new Entry {
    Placeholder = "Password",
    IsPassword = true
  };

  return new StackLayout {
    Children = {
      usernameEntry,
      passwordEntry
    }
  };
}

